function test_file_one(from, to) {
	var a = Math.abs(from.x - to.x);
	var b = Math.abs(from.y - to.y);

	if (a > 2) {
		if (b < 2) {
			return true;
		} else {
			return false;
		}
	}
}

function test_file_two(board) {
	var a = count_red_pieces(board);
	var b = count_blue_pieces(board);

	let diff = Math.abs(a - b);

	switch (diff) {
		case 1:
			return true;
		case 2, 3:
			return false;
		default:
			return true;
	}
}

function test_file_three(board) {
	var a = board.count_red_pieces(board);
	var b = board.count_blue_pieces(board);
	var c = board.count_empty_spaces(board);

	if (isTriangle(a, b, c)) {
		return true;
	} else {
		if (b < c && b > a) {
			return true;
		} else if (b == a || b == c) {
			return true;
		} else {
			return false;
		}
	}
}

function test_file_four(board) {
	var a = board.find_first_red_piece(board)
	var b = board.find_first_blue_piece(board)

	if (a.position == b.position) {
		return true;
	} else if (a.position.x == -1 && a.position.y == -1) {
		return true;
	} else if (b.position.x == -1 && b.position.y == -1) {
		return true;
	} else {
		return false;
	}
}

function test_file_five(board) {
	var a = board.find_first_stack(board)

	if (a.stack.red + a.stack.blue % 2 == 0 && a.stack.red + a.stack.blue > 2) {
		return true;
	} else {
		return false;
	}
}

function test_file_six(board) {
	// Sorted by x, then y coordinate.
	var vertices = utils.find_blue_pieces(board)

	if (vertices.length != 3) {
		return;
	}

	var side_a = [vertices[0], vertices[1]],
	    side_b = [vertices[1], vertices[2]],
	    side_c = [vertices[2], vertices[0]];

	var side_a_length = utils.distance(side_a[0], side_a[1]),
	    side_b_length = utils.distance(side_b[0], side_b[1]),
	    side_c_length = utils.distance(side_c[0], side_c[1]);

	if (Math.sqrt(Math.pow(side_a_length, 2) + Math.pow(side_b_length, 2)) == side_c_length) {
		return true;
	} else if (Set([side_a_length, side_b_length, side_c_length]).size == 3) {
		return true;
	} else if (Set([side_a_length, side_b_length, side_c_length]).size == 2) {
		return true;
	} else if (Set([side_a_length, side_b_length, side_c_length]).size == 1 && side_a_length == 0) {
		return false;
	}
}
